if network:
    bitcoin.SelectParams(network)

class Bitcoind:

    MINCONF = settings.BTC_MIN_CONFIRMATIONS
    VIEW_URL = settings.BTC_VIEW_URL
    URL = settings.BITCOIND_URL

    def __init__(self):
        try:
            url = self.URL
        except:
            url = None
            
        self.proxy = bitcoin.rpc.Proxy(service_url=url)
    @staticmethod
    def usd_to_btc(usd):
        return rate.usd_to_btc(usd)

