+        // Only do this if there was a utxo
+        if (!utxo.IsNull()) {
+            // Put the UTXO in the merged psbtx
+            if (non_witness_utxo) {
+                merged_psbtx.inputs.at(i).non_witness_utxo = non_witness_utxo;
+            } else {
+                merged_psbtx.inputs.at(i).witness_utxo = utxo;
+            }
