+{
+    for (auto keypath_pair : hd_keypaths) {
+        SerializeToVector(s, type, MakeSpan(keypath_pair.first));
+        WriteCompactSize(s, keypath_pair.second.size() * sizeof(uint32_t));
+        for (auto& path : keypath_pair.second) {
+            s << path;
+        }
+    }
