+            }
+            if (IsRange()) {
+                key.Derive(key, pos);
+            }
+            out = key.pubkey;
+        }
+        return true;
+    }
+    std::string ToString() const override
+    {
+        std::string ret = EncodeExtPubKey(m_extkey);
+        for (auto entry : m_path) {
+            ret += strprintf("/%i", entry & 0x7FFFFFFFUL);
+            if (entry >> 31) ret += '\'';
+        }
+        if (IsRange()) {
+            ret += "/*";
+            if (m_derive == DeriveType::HARDENED) ret += '\'';
+        }
+        return ret;
+    }
