+    {
+        CExtKey key;
+        if (!GetExtKey(arg, key)) return false;
+        out = EncodeExtKey(key);
+        for (auto entry : m_path) {
+            out += strprintf("/%i", entry & 0x7FFFFFFFUL);
+            if (entry >> 31) out += '\'';
+        }
+        if (IsRange()) {
+            out += "/*";
+            if (m_derive == DeriveType::HARDENED) out += '\'';
+        }
+        return true;
+    }
