+            case NodeType::WRAP_C: return subs[0]->ss;
+            case NodeType::WRAP_D: return {1 + subs[0]->ss.sat, 1};
+            case NodeType::WRAP_V: return {subs[0]->ss.sat, {}};
+            case NodeType::WRAP_J: return {subs[0]->ss.sat, 1};
