nit: there is no guarantee now, that `parent` is a top-level widget. 

May I suggest a following diff:
```diff
diff --git a/src/qt/utilitydialog.cpp b/src/qt/utilitydialog.cpp
index 65e8823cb..a1ce8e741 100644
--- a/src/qt/utilitydialog.cpp
+++ b/src/qt/utilitydialog.cpp
@@ -19,10 +19,12 @@
 
 #include <QCloseEvent>
 #include <QLabel>
+#include <QMainWindow>
 #include <QRegExp>
 #include <QTextTable>
 #include <QTextCursor>
 #include <QVBoxLayout>
+#include <QWidget>
 
 /** "Help message" or "About" dialog box */
 HelpMessageDialog::HelpMessageDialog(interfaces::Node& node, QWidget *parent, bool about) :
@@ -142,16 +144,16 @@ ShutdownWindow::ShutdownWindow(QWidget *parent, Qt::WindowFlags f):
     setLayout(layout);
 }
 
-QWidget* ShutdownWindow::showShutdownWindow(QWidget* parent)
+QWidget* ShutdownWindow::showShutdownWindow(QMainWindow* window)
 {
-    assert(parent != nullptr);
+    assert(window);
 
     // Show a simple window indicating shutdown status
     QWidget *shutdownWindow = new ShutdownWindow();
-    shutdownWindow->setWindowTitle(parent->windowTitle());
+    shutdownWindow->setWindowTitle(window->windowTitle());
 
     // Center shutdown window at where main window was
-    const QPoint global = parent->mapToGlobal(parent->rect().center());
+    const QPoint global = window->mapToGlobal(window->rect().center());
     shutdownWindow->move(global.x() - shutdownWindow->width() / 2, global.y() - shutdownWindow->height() / 2);
     shutdownWindow->show();
     return shutdownWindow;
